DROP TABLE IF EXISTS `vw_empresas_stats`;
CREATE ALGORITHM=UNDEFINED SQL SECURITY DEFINER VIEW `vw_empresas_stats` AS SELECT 
    e.id,
    e.nome,
    e.tipo,
    e.caixa,
    e.limite_inventario,
    COUNT(DISTINCT ef.char_id) as total_funcionarios,
    COUNT(DISTINCT ec.id) as total_cargos,
    COALESCE(SUM(CASE WHEN em.tipo IN ('deposito', 'admin_credito') THEN em.valor ELSE 0 END), 0) as total_creditos,
    COALESCE(SUM(CASE WHEN em.tipo IN ('retirada', 'pagamento', 'admin_debito') THEN em.valor ELSE 0 END), 0) as total_debitos,
    e.data_criacao,
    e.data_atualizacao
FROM empresas e
LEFT JOIN empresa_funcionarios ef ON e.id = ef.empresa_id
LEFT JOIN empresa_cargos ec ON e.id = ec.empresa_id
LEFT JOIN empresa_movimentacoes em ON e.id = em.empresa_id
WHERE e.ativa = TRUE
GROUP BY e.id, e.nome, e.tipo, e.caixa, e.limite_inventario, e.data_criacao, e.data_atualizacao 
;

